mapper.select:
SELECT MEMBER_IDX
FROM MEMBER
WHERE MEMBER_EMAIL = #{member_email}

mapper.update:
UPDATE member
member_intro = #{member_intro}, member_info = #{member_info}, member_info_image = #{member_info_image},
WHERE member_idx = #{member_idx}

UPDATE member
SET member_image = #{imageUrl}
WHERE member_idx = #{memberIdx}
