mapper.insert:
SELECT message_seq.NEXTVAL FROM DUAL
INSERT INTO message ( message_idx, room_idx, sender_idx, receiver_idx, message_content, message_type, message_senddate, message_delete, message_order, parent_idx
,attach_idx
) VALUES ( #{message_idx}, #{room_idx}, #{sender_idx}, #{receiver_idx}, #{message_content}, #{message_type}, SYSTIMESTAMP, 'N', #{message_order}, #{parent_idx}
,#{attach_idx}
)

mapper.select:
SELECT * FROM (
    SELECT ROWNUM rn, m.* FROM (
        SELECT * FROM message 
        WHERE room_idx = #{room_idx} AND message_delete = 'N'
        ORDER BY message_senddate DESC
    ) m WHERE ROWNUM <= #{limit}
) WHERE rn > #{offset}
ORDER BY message_senddate ASC

SELECT c.* FROM chatattach c 
INNER JOIN message m ON c.attach_idx = m.attach_idx 
WHERE m.message_idx = #{message_idx}
